#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Sat Mar  1 08:51:37 2025@author: unimbecileee"""import cv2 as cv# Charger la cascade de Haar pour la détection de visagesface_cascade = cv.CascadeClassifier('haarcascade_frontalface_default.xml')eye_cascade = cv.CascadeClassifier('haarcascade_eye.xml')# Charger les imagesimg = cv.imread('/Volumes/Landry/Projet-ai/face-detect/obama.jpg')# Vérifier si l'image a été chargée correctementif img is None:    print("Erreur : Impossible de charger l'image. Veuillez vérifier le chemin et le nom de fichier.")else:    # Convertir l'image en niveaux de gris    gray = cv.cvtColor(img, cv.COLOR_BGR2GRAY)    # Exécution de la détection de visage    faces = face_cascade.detectMultiScale(gray, 1.1, 8)    # Afficher les visages    for face in faces:        x, y, w, h = face                # dessiner le rectangle sur l'image principale        cv.rectangle(img, (x, y), (x+w, y+h), (0, 255, 0), 2)        # Execution detection des yeuxeyes = eye_cascade.detectMultiScale(gray, 1.1, 3)    #affichage des yeuxfor (ex, ey, ew, eh) in eyes:    #dessiner le rectangle autour des yeux sur l'image principale    cv.rectangle(img, (ex, ey), (ex + ew, ey + eh), (255, 0, 0), 2)        # Afficher l'image avec les visages détectéscv.imshow('image pincipale', img)cv.waitKey(0)cv.destroyAllWindows()